
public class classMethodMapping {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
/*
 * Math operator functions
 */
		//pow 5^3 = 5*5*5 /*Multiples the power times
		System.out.println(Math.pow(2, 5));
		
		//abs -5 = 5 /*Converts Negative to positive
		System.out.println(Math.abs(-3.5));
		
		//ceil /*Rounds off to next number
		System.out.println(Math.ceil(3.5));
		
		//floor /*Rounds off to previous number
		System.out.println(Math.floor(3.5));
		
		//max /*Maximum of numbers
		System.out.println(Math.max(25.5, 25.49));
		
		//min /*Minimum of numbers
		System.out.println(Math.min(25.5, 25.49));
		
		//sqrt /*Square root of the number
		System.out.println(Math.sqrt(25));
	}

}
